import * as React from "react";

interface StyleDeclaration {
    [key: string]: React.CSSProperties;
}

interface Properties {
  [name: string]: any;
}

interface ClassMap {
  [name: string]: string;
}

interface StyleSheet {
  create(styles: StyleDeclaration): (props?: Properties, state?: Properties) => string;
  createNamed(className: string, styles: StyleDeclaration): (props?: Properties, state?: Properties) => string;
  createClassMap(classMap: ClassMap): (props?: Properties, state?: Properties) => string;
  createNative(styles: StyleDeclaration): (props?: Properties, state?: Properties) => StyleDeclaration;
}

export var StyleSheet: StyleSheet
